import React, { useState, useContext } from "react";
import { Context } from "../App";
import Delete from "../icons/delete";
import Update from "../icons/update";
import "../App.css";

export default function Task(props) {
  const context = useContext(Context);
  const [check, setCheck] = useState(false);

  const handleCheck = () => {
    setCheck(!check);
  };

  const deleteTask = () => {
    const index = context.lists.tasks.indexOf(props.task);
    if (index !== -1) {
      const deletedTask = context.lists.tasks[index];
      context.lists.tasks.splice(index, 1);
      const newLists = {
        ...context.lists,
        deletedTasks: [...context.lists.deletedTasks, deletedTask],
      };
    } else {
      const deletedTask = context.lists.tasks[index];
      context.lists.tasks.splice(index, 1);
      const newLists = {
        ...context.lists,
        deletedTasks: [...context.lists.deletedTasks, deletedTask],
      };
    }

    context.setLists(newLists);
  };

  return (
    <div className="Task-div">
      <label
        className="Task"
        style={{ textDecoration: check ? "line-through" : "none" }}
      >
        <input
          className="Task-checkbox"
          type="checkbox"
          onChange={() => handleCheck()}
        ></input>
        {props.task}
      </label>

      <div className="Task-elements">
        <Update />
        <a onClick={() => deleteTask()}>
          <Delete />
        </a>
      </div>
    </div>
  );
}
